mapper.select:
select 

from SYS_CODE


   AND CODE_ID = #{codeId,jdbcType=VARCHAR}


   AND CODE = #{code,jdbcType=VARCHAR}


   AND CODE_TYPE = #{codeType,jdbcType=VARCHAR}


   AND CODE_VALUE = #{codeValue,jdbcType=VARCHAR}


   AND CODE_VALUE_1 = #{codeValue1,jdbcType=VARCHAR}


   AND CODE_VALUE_2 = #{codeValue2,jdbcType=VARCHAR}


   AND ORDER_BY = #{orderBy,jdbcType=DECIMAL}


   AND PAR_CODE_ID = #{parCodeId,jdbcType=VARCHAR}


   AND VALID_FLAG = #{validFlag,jdbcType=CHAR}


   AND CREATED_BY = #{createdBy,jdbcType=VARCHAR}


   AND CREATED_TS = #{createdTs,jdbcType=TIMESTAMP}


   AND LAST_UPD_BY = #{lastUpdBy,jdbcType=VARCHAR}


   AND LAST_UPD_TS = #{lastUpdTs,jdbcType=TIMESTAMP}
